/**
 * Filter gform_gf_query_sql
 *
 * @param array $sql An array with all the SQL fragments: select, from, join, where, order, paginate.
 * @return array Modified sql
 */
add_filter( 'gform_gf_query_sql', 'my_custom_gform_gf_query_sql_filter', 10, 1 );

function my_custom_gform_gf_query_sql_filter($sql) {
    // Modify $sql as needed

    return $sql;
}

